mapper.select:
	select *
	from (
	 select rownum cnt,  r.*  
	 from RM r 
	 WHERE 1=1

	 AND pname LIKE '%'||#{pname}||'%'

	 ORDER BY RM_no ASC
	  )
    where cnt between #{start} and #{end}

mapper.update:
update RM
set readcnt = readcnt+1
where RM_no = #{RM_no}

mapper.select:
SELECT name
FROM   Mem4pro
WHERE  empno = #{empno}
